package com.tobadiahstane.tennis.player;

public class Player {

	private final int id;
	private final String name;
	
	public Player (int id, String name) {
		this.name = name;
		this.id = id;
	}

	public String getName() {
		// TODO Auto-generated method stub
		return name;
	}

	public int getId() {
		// TODO Auto-generated method stub
		return id;
	}

}
